CREATE TABLE IF NOT EXISTS  `hr` (`id` varchar(36) NOT NULL COMMENT '联系人唯一标识',`name` varchar(54) NULL COMMENT '姓名',`unit` varchar(80) NULL COMMENT '所属公司',`contact` varchar(120) NULL COMMENT '联系方式',`sex` varchar(8) NULL COMMENT '性别',`createtime` datetime NULL COMMENT '创建时间',`createuser` varchar(36) NULL COMMENT '创建用户',`updatetime` datetime NULL COMMENT '更新时间',`updateuser` varchar(36) NULL COMMENT '更新日期',PRIMARY KEY (`id`) )COMMENT = '求职联系人';CREATE TABLE IF NOT EXISTS  `job` (`id` varchar(36) NOT NULL COMMENT '工作唯一标识',`hrid` varchar(36) NOT NULL COMMENT '工作推荐人',`name` varchar(50) NULL COMMENT '工作名称',`unit` varchar(80) NULL COMMENT '工作所属的单位',`address` varchar(150) NULL COMMENT '工作地址',`worktime` varchar(120) NULL COMMENT '工作时间安排',`welfare` varchar(500) NULL COMMENT '福利待遇说明',`salary` varchar(100) NULL COMMENT '工资说明',`descrip` varchar(500) NULL COMMENT '工作职责说明',`skill` varchar(500) NULL COMMENT '技能要求',`state` varchar(10) NULL COMMENT '工作状态；已回绝；待面试；面试失败；待复试；得到offer',PRIMARY KEY (`id`) )COMMENT = '工作信息';CREATE TABLE IF NOT EXISTS  `interview` (`id` varchar(36) NOT NULL COMMENT '面试记录唯一标识',`jobid` varchar(36) NULL COMMENT '所属工作',`name` varchar(54) NULL COMMENT '面试官姓名',`intertime` datetime NULL  COMMENT '面试时间',`content` text NULL COMMENT '面试交谈内容',`state` varchar(10) NULL COMMENT '面试最终的结果',`userid` varchar(36) NULL COMMENT '求职人员',PRIMARY KEY (`id`) )COMMENT = '面试记录表';