CREATE TABLE IF NOT EXISTS `user` (`id` varchar(36) NOT NULL COMMENT '唯一标识',`name` varchar(20) NULL COMMENT '用户名',`age` int(11) NULL COMMENT '年龄',`sex` tinyint(4) NULL DEFAULT 0 COMMENT '性别',`pwd` varchar(45) NULL DEFAULT 123456 COMMENT '密码',PRIMARY KEY (`id`) )COMMENT = '用户表';CREATE TABLE IF NOT EXISTS `role` (`id` varchar(36) NOT NULL COMMENT '唯一标识',`name` varchar(20) NULL COMMENT '角色名称',`description` varchar(50) NULL COMMENT '角色描述',PRIMARY KEY (`id`) )COMMENT = '角色表';CREATE TABLE IF NOT EXISTS `permission` (`id` varchar(36) NOT NULL,`name` varchar(45) NULL COMMENT '权限名',`code` varchar(45) NULL COMMENT '权限码值',`desp` varchar(45) NULL COMMENT '权限描述',`url` varchar(100) NULL COMMENT '权限地址',PRIMARY KEY (`id`) )COMMENT = '权限表';CREATE TABLE IF NOT EXISTS `user_role` (`userid` varchar(36) NOT NULL COMMENT '用户id',`roleid` varchar(36) NOT NULL COMMENT '角色id',PRIMARY KEY (`userid`, `roleid`) )COMMENT = '用户角色关联表';CREATE TABLE IF NOT EXISTS `role_permission` (`roleid` varchar(36) NOT NULL COMMENT '角色id',`permid` varchar(36) NOT NULL COMMENT '权限id',PRIMARY KEY (`roleid`, `permid`) )COMMENT = '角色权限关联表';